sysevent_script_action.script:
gs.info("Running ServiceNow for Slack script for problem subcription - " + event.parm1 + " and id -" + current.getValue("sys_id"));

var serviceNowForSlackUtils = new ServiceNowForSlackUtils();

// get valid bulk alert subscriptions
var bulkSubscriptions = serviceNowForSlackUtils.getBulkAlertsSubscriptions(current.sys_class_name, event.parm1);
while (bulkSubscriptions.next()) { 
	// call endpoint to send bulk subscriptions
	var bulkNotificationRecord = serviceNowForSlackUtils.getSlackNotificationRecord(bulkSubscriptions, current);
	gs.info("Calling Slack Api to send bulk subscriptions for record - " + JSON.stringify(bulkNotificationRecord));
	var response = serviceNowForSlackUtils.sendSlackNotification(bulkNotificationRecord);
}
